using System.Collections.Generic;
using Baltic.DataModel.CAL;

namespace Baltic.DataModel.CALExecutable {
	public abstract class CDataToken {

		public string Uid;
		public long TokenNo;
		public string PinUid;
		public string PinName;
		public DataBinding Binding;
		public DataMultiplicity Multiplicity;
		public List<int> Depths;
		public bool Direct;
		public abstract CDataSet DataSet {get; set;}

		public CDataToken(){
			Depths = new List<int>();
		}

		~CDataToken(){

		}

		public virtual void Dispose(){

		}
		
		public override string ToString() {
			string ret = "DataToken " + PinName + " " + PinUid + " " + TokenNo + " " + Binding + " " + Multiplicity +
				" dir=" + Direct;
			foreach (int i in Depths)
				ret += (0 == Depths.IndexOf(i) ? " #depths: " : ",") + i;
			return ret + "\n";
		}

	}//end DataToken

}//end namespace CAL_Executable