using Baltic.Engine.Cluster.JobManager;

namespace Baltic.Engine.Cluster.Cluster
{
	public class ClusterProxy : ICluster {

		// MOCK
		public ITokenMQ Mq;
		// MOCK

		public ClusterProxy(){

		}

		~ClusterProxy(){

		}

		/// 
		/// <param name="YAML"></param>
		public IJob StartJob(string YAML){
			Job.Job job = new Job.Job();
			job.init(YAML);
			return (IJob) job;
		}

		/// 
		/// <param name="YAML"></param>
		public IJobs StartBatch(string YAML){
			JobManager.JobManager jm = new JobManager.JobManager();
			jm.Init(Mq);
			return jm;
		}

	}
}//end ClusterProxyAdapter